mapper.select:
SELECT res.RES_ID id, res.RES_NAME name, res.RES_PARENT_ID parentId, res.RES_KEY resKey, res.RES_URL resUrl, res.RES_TYPE type, res.RES_LEVEL level, res.RES_DES description, ress.RES_NAME pName FROM SYS_RESOURCES res LEFT JOIN SYS_RESOURCES ress ON res.RES_PARENT_ID =ress.RES_ID WHERE 1=1
AND res.RES_NAME like '%${resName}%'
limit #{startPage},#{pageSize}
